import React, { useEffect, useState } from 'react';

const clamp = (val, min, max) => Math.max(min, Math.min(max, val));

export default function PageZoomControl() {
  const [size, setSize] = useState(16);

  useEffect(() => {
    const saved = Number(localStorage.getItem('app_base_font'));
    if (saved && !Number.isNaN(saved)) {
      setSize(saved);
      document.documentElement.style.setProperty('--app-base-font', `${saved}px`);
    } else {
      document.documentElement.style.setProperty('--app-base-font', `${size}px`);
    }
  }, []);

  const apply = (next) => {
    const val = clamp(next, 12, 22);
    setSize(val);
    document.documentElement.style.setProperty('--app-base-font', `${val}px`);
    localStorage.setItem('app_base_font', String(val));
  };

  return (
    <div style={wrap}>
      <button style={btn} onClick={() => apply(size - 1)}>-</button>
      <div style={valBox}>{size}px</div>
      <button style={btn} onClick={() => apply(size + 1)}>+</button>
      <button style={{ ...btn, marginLeft: 6 }} onClick={() => apply(16)}>Reset</button>
    </div>
  );
}

const wrap = {
  position: 'fixed',
  right: 16,
  top: 16,
  zIndex: 3000,
  background: 'rgba(45, 52, 54, 0.9)',
  color: '#fff',
  borderRadius: 10,
  display: 'flex',
  alignItems: 'center',
  gap: 6,
  padding: '6px 8px',
  boxShadow: '0 6px 20px rgba(0,0,0,0.25)'
};

const btn = {
  background: '#1f7a8c',
  color: '#fff',
  border: 'none',
  borderRadius: 6,
  padding: '6px 8px',
  cursor: 'pointer',
  fontWeight: 'bold'
};

const valBox = {
  minWidth: 48,
  textAlign: 'center'
};
