import React, { useEffect, useMemo, useRef, useState } from 'react';
import { onAuthStateChanged } from 'firebase/auth';
import { auth, db } from '../firebase';
import { collection, query, where, onSnapshot, doc, updateDoc } from 'firebase/firestore';

export default function DriverPortal() {
  const [user, setUser] = useState(null);
  const [assignment, setAssignment] = useState(null);
  const [driver, setDriver] = useState(null);
  const [driverDocId, setDriverDocId] = useState(null);
  const [activeTab, setActiveTab] = useState('assignment'); // 'assignment' | 'profile'
  const [profileForm, setProfileForm] = useState({ name: '', phone: '', vehicleType: '' });
  const [notification, setNotification] = useState(null);
  const lastAssignedRef = useRef(null);

  useEffect(() => {
    const unsubAuth = onAuthStateChanged(auth, (u) => setUser(u));
    return () => unsubAuth();
  }, []);

  useEffect(() => {
    if (!user) return;
    // Find driver doc by uid or email
    const q = query(collection(db, 'drivers'), where('email', '==', user.email || ''));
    const unsub = onSnapshot(q, (snap) => {
      const d = snap.docs[0];
      const data = d?.data();
      setDriver(data || null);
      setDriverDocId(d?.id || null);
      if (!data) { setAssignment(null); return; }
      setProfileForm({
        name: data.name || '',
        phone: data.phone || '',
        vehicleType: data.vehicleType || '',
      });
      // Watch the request assigned to this driver via assignedVehicle
      const rq = query(
        collection(db, 'emergencyRequests'),
        where('assignedVehicle', '==', data.assignedVehicle || '__none__')
      );
      const unsubReq = onSnapshot(rq, (rs) => {
        const next = rs.docs[0] ? { id: rs.docs[0].id, ...rs.docs[0].data() } : null;
        // Show notification on new assignment
        if (next?.assignedVehicle && lastAssignedRef.current !== next.assignedVehicle) {
          setNotification({
            title: 'New Assignment',
            message: `Vehicle ${next.assignedVehicle} assigned.`,
            type: 'info'
          });
          lastAssignedRef.current = next.assignedVehicle;
        }
        setAssignment(next);
      });
      return () => unsubReq();
    });
    return () => unsub();
  }, [user]);

  const shareLocation = () => {
    if (!navigator.geolocation || !user) return;
    navigator.geolocation.getCurrentPosition(async (pos) => {
      const { latitude, longitude } = pos.coords;
      const q = query(collection(db, 'drivers'), where('email', '==', user.email || ''));
      // Fetch once via onSnapshot callback pattern
      const unsub = onSnapshot(q, async (snap) => {
        const driverDoc = snap.docs[0];
        if (driverDoc) {
          await updateDoc(doc(db, 'drivers', driverDoc.id), {
            location: { latitude, longitude },
            lastSharedAt: new Date().toISOString(),
          });
          setNotification({ title: 'Location Shared', message: 'Your current location was sent.', type: 'success' });
        }
        unsub();
      });
    });
  };

  const saveProfile = async () => {
    if (!driverDocId) return;
    await updateDoc(doc(db, 'drivers', driverDocId), {
      name: profileForm.name,
      phone: profileForm.phone,
      vehicleType: profileForm.vehicleType,
    });
    setNotification({ title: 'Profile Updated', message: 'Your profile was saved.', type: 'success' });
  };

  const assignmentMapUrl = useMemo(() => {
    if (!assignment?.latitude || !assignment?.longitude) return null;
    return `https://www.google.com/maps?q=${assignment.latitude},${assignment.longitude}&hl=es;z=14&output=embed`;
  }, [assignment]);

  return (
    <div style={styles.container}>
      <aside style={styles.sidebar}>
        <h3 style={{ marginTop: 0 }}>Driver</h3>
        <ul style={styles.menu}>
          <li style={{ ...styles.menuItem, ...(activeTab === 'assignment' ? styles.menuItemActive : {}) }} onClick={() => setActiveTab('assignment')}>My Assignment</li>
          <li style={{ ...styles.menuItem, ...(activeTab === 'profile' ? styles.menuItemActive : {}) }} onClick={() => setActiveTab('profile')}>Profile</li>
        </ul>
      </aside>
      <main style={styles.main}>
        <h2 style={{ marginTop: 0 }}>Driver Portal</h2>
        {!user && <p>Please log in.</p>}
        {user && activeTab === 'assignment' && (
          <div style={styles.card}>
            <p><strong>User:</strong> {user.email}</p>
            {!assignment ? (
              <p>No vehicle assigned yet.</p>
            ) : (
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 12 }}>
                <div>
                  <p><strong>Assigned Vehicle:</strong> {assignment.assignedVehicle}</p>
                  <p><strong>Type:</strong> {assignment.assignedVehicleType || '-'}</p>
                  <p><strong>Status:</strong> {assignment.status}</p>
                </div>
                <div>
                  <p><strong>Destination:</strong> {assignment.latitude}, {assignment.longitude}</p>
                  {assignmentMapUrl && (
                    <iframe title="destination" width="100%" height="180" style={{ border: 0 }} loading="lazy" src={assignmentMapUrl}></iframe>
                  )}
                </div>
              </div>
            )}
            <div style={{ marginTop: 12 }}>
              <button style={styles.button} onClick={shareLocation}>Share Current Location</button>
            </div>
          </div>
        )}
        {user && activeTab === 'profile' && (
          <div style={styles.card}>
            <h3 style={{ marginTop: 0 }}>My Profile</h3>
            <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr 1fr', gap: 12 }}>
              <div>
                <label>Name</label>
                <input style={styles.input} value={profileForm.name} onChange={(e) => setProfileForm({ ...profileForm, name: e.target.value })} />
              </div>
              <div>
                <label>Phone</label>
                <input style={styles.input} value={profileForm.phone} onChange={(e) => setProfileForm({ ...profileForm, phone: e.target.value })} />
              </div>
              <div>
                <label>Vehicle Type</label>
                <select style={styles.input} value={profileForm.vehicleType} onChange={(e) => setProfileForm({ ...profileForm, vehicleType: e.target.value })}>
                  <option value="ambulance">Ambulance</option>
                  <option value="fireengine">Fire Engine</option>
                  <option value="policevan">Police Van</option>
                </select>
              </div>
            </div>
            <div style={{ marginTop: 12 }}>
              <button style={styles.button} onClick={saveProfile}>Save</button>
            </div>
          </div>
        )}
        {notification && (
          <div style={{ position: 'fixed', right: 16, bottom: 16, background: '#2d3436', color: '#fff', padding: '10px 12px', borderRadius: 8, boxShadow: '0 6px 20px rgba(0,0,0,0.25)' }}>
            <div style={{ fontWeight: 'bold' }}>{notification.title}</div>
            <div>{notification.message}</div>
            <button style={{ marginTop: 6, background: '#636e72', color: '#fff', border: 'none', borderRadius: 6, padding: '4px 8px', cursor: 'pointer' }} onClick={() => setNotification(null)}>Close</button>
          </div>
        )}
      </main>
    </div>
  );
}

