import React, { useState } from 'react';
import { useForm, ValidationError } from '@formspree/react';
import Navbar from '../components/Navbar';
import { db } from '../firebase';
import { addDoc, collection, serverTimestamp } from 'firebase/firestore';

function Contact() {
  const [state, handleSubmit] = useForm("xpwlyvan");
  const [chatMessages, setChatMessages] = useState([]);
  const [chatInput, setChatInput] = useState("");
  const [emailForReport, setEmailForReport] = useState("");

  const onSendChat = () => {
    if (!chatInput.trim()) return;
    setChatMessages((prev) => [...prev, { role: 'user', text: chatInput.trim(), ts: Date.now() }]);
    setChatInput("");
  };

  const onSubmitReport = async () => {
    if (chatMessages.length === 0) return;
    try {
      await addDoc(collection(db, 'reports'), {
        email: emailForReport || null,
        messages: chatMessages,
        createdAt: serverTimestamp(),
        status: 'open',
      });
      setChatMessages([]);
      alert('Report submitted to admin.');
    } catch (e) {
      alert('Failed to submit report.');
    }
  };

  return (
    <>
      <Navbar />
      <div style={styles.container}>
        <h2 style={styles.heading}>Contact Us</h2>

        {state.succeeded ? (
          <p style={styles.success}>‚úÖ Thank you! We'll get back to you soon.</p>
        ) : (
          <form onSubmit={handleSubmit} style={styles.form}>
            <label htmlFor="email" style={styles.label}>Email Address</label>
            <input
              id="email"
              type="email"
              name="email"
              required
              style={styles.input}
              onChange={(e) => setEmailForReport(e.target.value)}
            />
            <ValidationError prefix="Email" field="email" errors={state.errors} />

            <label htmlFor="message" style={styles.label}>Message</label>
            <textarea
              id="message"
              name="message"
              required
              rows="5"
              style={styles.textarea}
            />
            <ValidationError prefix="Message" field="message" errors={state.errors} />

            <button type="submit" disabled={state.submitting} style={styles.button}>
              {state.submitting ? 'Sending...' : 'Submit'}
            </button>
          </form>
        )}

        <h3 style={styles.reviewHeading}>What people are saying</h3>
        <div style={styles.reviews}>
          <div style={styles.reviewCard}>
            <p>üöë ‚ÄúFast and reliable emergency response. Really helped us in a tough time.‚Äù</p>
            <span>- Arjun Reddy</span>
          </div>
          <div style={styles.reviewCard}>
            <p>üî• ‚ÄúCalled for fire help ‚Äî response was quick and coordinated. Highly recommended.‚Äù</p>
            <span>- Priya Sharma</span>
          </div>
          <div style={styles.reviewCard}>
            <p>üëÆ ‚ÄúPolice van reached within 5 minutes. Hats off to the team.‚Äù</p>
            <span>- Rakesh Kumar</span>
          </div>
        </div>

        {/* Chatbot/Support */}
        <div style={styles.chatCard}>
          <h3 style={{ marginTop: 0 }}>Chat with Support</h3>
          <div style={styles.chatBox}>
            {chatMessages.length === 0 ? (
              <div style={{ color: '#888', textAlign: 'center' }}>Start typing your issue below‚Ä¶</div>
            ) : (
              chatMessages.map((m, idx) => (
                <div key={idx} style={{ display: 'flex', justifyContent: m.role === 'user' ? 'flex-end' : 'flex-start' }}>
                  <div style={{
                    background: m.role === 'user' ? '#007BFF' : '#e9ecef',
                    color: m.role === 'user' ? '#fff' : '#333',
                    padding: '8px 12px', borderRadius: 12, margin: '6px 0', maxWidth: '70%'
                  }}>
                    {m.text}
                  </div>
                </div>
              ))
            )}
          </div>
          <div style={styles.chatInputRow}>
            <input
              value={chatInput}
              onChange={(e) => setChatInput(e.target.value)}
              placeholder="Describe your problem‚Ä¶"
              style={styles.chatInput}
              onKeyDown={(e) => { if (e.key === 'Enter') { e.preventDefault(); onSendChat(); } }}
            />
            <button type="button" style={styles.chatSend} onClick={onSendChat}>Send</button>
            <button type="button" style={styles.chatReport} onClick={onSubmitReport}>Submit Report</button>
          </div>
          <div style={{ fontSize: 12, color: '#666', marginTop: 6 }}>Reports are forwarded to the admin Reports page.</div>
        </div>
      </div>
    </>
  );
}

const styles = {
  container: {
    padding: '40px 5%',
    fontFamily: 'Arial, sans-serif',
    maxWidth: '100%',
    boxSizing: 'border-box'
  },
  heading: {
    textAlign: 'center',
    fontSize: '2rem',
    marginBottom: '30px'
  },
  label: {
    fontWeight: 'bold',
    marginTop: '10px'
  },
  form: {
    maxWidth: '600px',
    margin: '0 auto',
    display: 'flex',
    flexDirection: 'column',
    gap: '15px'
  },
  input: {
    padding: '12px',
    fontSize: '16px',
    border: '1px solid #ccc',
    borderRadius: '5px'
  },
  textarea: {
    padding: '12px',
    fontSize: '16px',
    border: '1px solid #ccc',
    borderRadius: '5px',
    resize: 'none'
  },
  button: {
    padding: '12px',
    fontSize: '16px',
    backgroundColor: '#007BFF',
    color: '#fff',
    border: 'none',
    borderRadius: '5px',
    cursor: 'pointer'
  },
  success: {
    color: 'green',
    fontSize: '18px',
    textAlign: 'center'
  },
  reviewHeading: {
    marginTop: '50px',
    textAlign: 'center'
  },
  reviews: {
    marginTop: '20px',
    display: 'flex',
    flexWrap: 'wrap',
    gap: '20px',
    justifyContent: 'center'
  },
  reviewCard: {
    backgroundColor: '#f9f9f9',
    padding: '20px',
    borderRadius: '8px',
    boxShadow: '0 2px 8px rgba(0,0,0,0.1)',
    maxWidth: '300px',
    textAlign: 'left'
  },
  chatCard: {
    marginTop: '40px',
    background: '#fff',
    border: '1px solid #eee',
    borderRadius: 12,
    padding: 16,
    boxShadow: '0 6px 16px rgba(0,0,0,0.06)',
    maxWidth: 800,
    marginLeft: 'auto',
    marginRight: 'auto'
  },
  chatBox: {
    height: 220,
    overflowY: 'auto',
    border: '1px solid #eee',
    borderRadius: 10,
    padding: 12,
    background: '#fafafa'
  },
  chatInputRow: {
    display: 'flex',
    gap: 8,
    marginTop: 10
  },
  chatInput: {
    flex: 1,
    padding: '10px 12px',
    border: '1px solid #ccc',
    borderRadius: 8
  },
  chatSend: {
    padding: '10px 12px',
    border: 'none',
    borderRadius: 8,
    background: '#1f7a8c',
    color: '#fff',
    cursor: 'pointer',
    fontWeight: 'bold'
  },
  chatReport: {
    padding: '10px 12px',
    border: 'none',
    borderRadius: 8,
    background: '#e63946',
    color: '#fff',
    cursor: 'pointer',
    fontWeight: 'bold'
  }
};

export default Contact;
