import React, { useState, useEffect } from 'react';
import { Link, useLocation } from 'react-router-dom';
import { db, authReady } from '../firebase';
import { collection, onSnapshot, query, where } from 'firebase/firestore';

const AdminNavbar = () => {
  const [assignedCount, setAssignedCount] = useState(0);
  const location = useLocation();

  useEffect(() => {
    let unsubscribe = null;
    let active = true;
    authReady.then(() => {
      if (!active) return;
      const q = query(
        collection(db, 'emergencyRequests'),
        where('status', '==', 'assigned')
      );
      unsubscribe = onSnapshot(q, (snap) => {
        setAssignedCount(snap.size);
      });
    });
    return () => {
      active = false;
      if (unsubscribe) unsubscribe();
    };
  }, []);

  const isActive = (path) =>
    location.pathname.toLowerCase() === path.toLowerCase();

  return (
    <nav style={styles.navbar}>
      <h2 style={styles.logo}>Admin Panel</h2>

      <div style={styles.navGroup}>
        {/* Navigation Section */}
        <div style={styles.sectionTitle}>Navigation</div>
        <ul style={styles.navLinks}>
          <li>
            <Link
              style={{
                ...styles.link,
                ...(isActive('/admin') ? styles.linkActive : {}),
              }}
              to="/admin"
            >
              Dashboard
            </Link>
          </li>
          <li>
            <Link
              style={{
                ...styles.link,
                ...(isActive('/requests') ? styles.linkActive : {}),
              }}
              to="/requests"
            >
              Requests
            </Link>
          </li>
          <li>
            <Link
              style={{
                ...styles.link,
                ...(isActive('/accepted-requests') ? styles.linkActive : {}),
              }}
              to="/accepted-requests"
            >
              Accepted Requests
            </Link>
          </li>
          <li>
            <Link
              style={{
                ...styles.link,
                ...(isActive('/AssignedVehicles') ? styles.linkActive : {}),
                display: 'flex',
                justifyContent: 'space-between',
                alignItems: 'center',
              }}
              to="/AssignedVehicles"
            >
              <span>Assigned Vehicles</span>
              {assignedCount > 0 && (
                <span style={styles.badge}>{assignedCount}</span>
              )}
            </Link>
          </li>
          <li>
            <Link
              style={{
                ...styles.link,
                ...(isActive('/reports') ? styles.linkActive : {}),
              }}
              to="/reports"
            >
              Reports
            </Link>
          </li>
        </ul>

        <div style={styles.divider} />

        {/* People Section */}
        <div style={styles.sectionTitle}>People</div>
        <ul style={styles.navLinks}>
          <li>
            <Link
              style={{
                ...styles.link,
                ...(isActive('/drivers') ? styles.linkActive : {}),
              }}
              to="/drivers"
            >
              Drivers
            </Link>
          </li>
          <li>
            <Link
              style={{
                ...styles.link,
                ...(isActive('/driver-login') ? styles.linkActive : {}),
              }}
              to="/driver-login"
            >
              Driver Login
            </Link>
          </li>
        </ul>

        <div style={styles.divider} />

        {/* Logout */}
        <ul style={styles.navLinks}>
          <li>
            <Link
              style={{
                ...styles.link,
                ...(isActive('/') ? styles.linkActive : {}),
              }}
              to="/"
            >
              Logout
            </Link>
          </li>
        </ul>
      </div>
    </nav>
  );
};

const styles = {
  navbar: {
    position: 'fixed',
    left: 0,
    top: 0,
    bottom: 0,
    width: '230px',
    background: 'linear-gradient(to bottom, #2c3e50, #2f3b4a)',
    color: '#fff',
    padding: '16px',
    boxSizing: 'border-box',
    display: 'flex',
    flexDirection: 'column',
    zIndex: 1000,
    overflowY: 'auto',
  },
  logo: {
    margin: 0,
    fontSize: '1.4rem',
    fontWeight: 'bold',
    paddingBottom: '12px',
    borderBottom: '1px solid rgba(255,255,255,0.2)',
    marginBottom: '12px',
    textAlign: 'center',
  },
  navGroup: {
    display: 'flex',
    flexDirection: 'column',
    gap: '14px',
  },
  navLinks: {
    listStyle: 'none',
    display: 'flex',
    flexDirection: 'column',
    gap: '10px',
    margin: 0,
    padding: 0,
  },
  link: {
    textDecoration: 'none',
    background: 'rgba(255,255,255,0.08)',
    border: '1px solid rgba(255,255,255,0.35)',
    borderRadius: '8px',
    color: '#fff',
    fontWeight: '500',
    cursor: 'pointer',
    padding: '10px 12px',
    fontSize: '0.95rem',
    transition: 'all 0.2s ease',
    boxShadow: '0 2px 6px rgba(0,0,0,0.15)',
    display: 'block',
    width: '100%',
    textAlign: 'left',
  },
  linkActive: {
    background: 'rgba(255,255,255,0.22)',
    borderColor: 'rgba(255,255,255,0.65)',
  },
  sectionTitle: {
    fontSize: '0.8rem',
    fontWeight: 'bold',
    opacity: 0.8,
    paddingLeft: '4px',
    marginBottom: '-2px',
  },
  divider: {
    height: 1,
    background: 'rgba(255,255,255,0.25)',
    margin: '6px 0',
  },
  badge: {
    backgroundColor: '#ff4444',
    color: '#fff',
    padding: '2px 8px',
    borderRadius: '999px',
    fontSize: '0.8rem',
    fontWeight: 'bold',
  },
};

export default AdminNavbar;
