import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { signInWithEmailAndPassword, sendPasswordResetEmail } from 'firebase/auth';
import { auth } from '../firebase';
import Navbar from '../components/Navbar';

export default function DriverLogin() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [info, setInfo] = useState('');
  const navigate = useNavigate();

  const onSubmit = async (e) => {
    e.preventDefault();
    setError('');
    try {
      await signInWithEmailAndPassword(auth, email, password);
      navigate('/driver');
    } catch {
      setError('Invalid credentials');
    }
  };

  const onForgot = async () => {
    setError('');
    setInfo('');
    if (!email) { setError('Enter your email to reset password'); return; }
    try {
      await sendPasswordResetEmail(auth, email);
      setInfo('Password reset email sent. Check your inbox.');
    } catch (e) {
      setError('Failed to send reset email.');
    }
  };

  return (
    <>
      <Navbar />
      <div style={styles.wrap}>
        <div style={styles.card}>
          <h2 style={styles.title}>Driver Login</h2>
          <form onSubmit={onSubmit} style={styles.form}>
            <input style={styles.input} placeholder="Email" type="email" value={email} onChange={(e) => setEmail(e.target.value)} />
            <input style={styles.input} placeholder="Password" type="password" value={password} onChange={(e) => setPassword(e.target.value)} />
            {error && <div style={styles.error}>{error}</div>}
            {info && <div style={styles.info}>{info}</div>}
            <button style={styles.button} type="submit">Login</button>
            <button type="button" onClick={onForgot} style={{ ...styles.button, background: '#6c757d' }}>Forgot Password?</button>
          </form>
        </div>
      </div>
    </>
  );
}

const styles = {
  wrap: {
    minHeight: '100vh',
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    background: 'linear-gradient(135deg, #141e30 0%, #243b55 100%)',
    padding: 20,
  },
  card: {
    width: 360,
    backgroundColor: 'rgba(255,255,255,0.06)',
    border: '1px solid rgba(255,255,255,0.25)',
    borderRadius: 12,
    padding: 24,
    color: 'white',
    boxShadow: '0 10px 30px rgba(0,0,0,0.35)'
  },
  title: {
    margin: 0,
    marginBottom: 16,
    textAlign: 'center',
  },
  form: {
    display: 'flex',
    flexDirection: 'column',
    gap: 12,
  },
  input: {
    padding: '12px 14px',
    borderRadius: 8,
    border: '1px solid rgba(255,255,255,0.35)',
    backgroundColor: 'rgba(0,0,0,0.2)',
    color: 'white'
  },
  error: { color: '#ff6b6b' },
  info: { color: '#38b000' },
  button: {
    padding: '12px 14px',
    borderRadius: 8,
    border: 'none',
    background: '#e63946',
    color: 'white',
    cursor: 'pointer',
    fontWeight: 'bold'
  }
};
