import React, { useEffect, useState } from 'react';
import { onAuthStateChanged } from 'firebase/auth';
import { auth, db } from '../firebase';
import { collection, query, where, onSnapshot, doc, updateDoc } from 'firebase/firestore';

export default function DriverPortal() {
  const [user, setUser] = useState(null);
  const [assignment, setAssignment] = useState(null);

  useEffect(() => {
    const unsubAuth = onAuthStateChanged(auth, (u) => setUser(u));
    return () => unsubAuth();
  }, []);

  useEffect(() => {
    if (!user) return;
    // Find driver doc by uid or email
    const q = query(collection(db, 'drivers'), where('email', '==', user.email || ''));
    const unsub = onSnapshot(q, (snap) => {
      const driver = snap.docs[0]?.data();
      if (!driver) { setAssignment(null); return; }
      // Watch the request assigned to this driver via assignedVehicle (optional)
      const rq = query(
        collection(db, 'emergencyRequests'),
        where('assignedVehicle', '==', driver.assignedVehicle || '__none__')
      );
      const unsubReq = onSnapshot(rq, (rs) => {
        setAssignment(rs.docs[0] ? { id: rs.docs[0].id, ...rs.docs[0].data() } : null);
      });
      return () => unsubReq();
    });
    return () => unsub();
  }, [user]);

  const shareLocation = () => {
    if (!navigator.geolocation || !user) return;
    navigator.geolocation.getCurrentPosition(async (pos) => {
      const { latitude, longitude } = pos.coords;
      const q = query(collection(db, 'drivers'), where('email', '==', user.email || ''));
      // Fetch once via onSnapshot callback pattern
      const unsub = onSnapshot(q, async (snap) => {
        const driverDoc = snap.docs[0];
        if (driverDoc) {
          await updateDoc(doc(db, 'drivers', driverDoc.id), {
            location: { latitude, longitude },
          });
        }
        unsub();
      });
    });
  };

  return (
    <div style={styles.container}>
      <aside style={styles.sidebar}>
        <h3 style={{ marginTop: 0 }}>Driver</h3>
        <ul style={styles.menu}>
          <li style={styles.menuItem}>My Assignment</li>
          <li style={styles.menuItem}>Profile</li>
        </ul>
      </aside>
      <main style={styles.main}>
        <h2 style={{ marginTop: 0 }}>Driver Portal</h2>
        {!user && <p>Please log in.</p>}
        {user && (
          <div style={styles.card}>
            <p><strong>User:</strong> {user.email}</p>
            {!assignment ? (
              <p>No vehicle assigned yet.</p>
            ) : (
              <div style={{ display: 'grid', gridTemplateColumns: '1fr 1fr', gap: 12 }}>
                <div>
                  <p><strong>Assigned Vehicle:</strong> {assignment.assignedVehicle}</p>
                  <p><strong>Type:</strong> {assignment.assignedVehicleType || '-'}</p>
                  <p><strong>Status:</strong> {assignment.status}</p>
                </div>
                <div>
                  <p><strong>Destination:</strong> {assignment.latitude}, {assignment.longitude}</p>
                  {assignment.latitude && assignment.longitude && (
                    <a href={`https://www.google.com/maps?q=${assignment.latitude},${assignment.longitude}`} target="_blank" rel="noreferrer">Open in Maps</a>
                  )}
                </div>
              </div>
            )}
            <div style={{ marginTop: 12 }}>
              <button style={styles.button} onClick={shareLocation}>Share Current Location</button>
            </div>
          </div>
        )}
      </main>
    </div>
  );
}

const styles = {
  container: { display: 'flex', minHeight: '100vh', background: '#f5f6fa' },
  sidebar: {
    position: 'fixed', left: 0, top: 0, bottom: 0, width: 220,
    background: 'linear-gradient(to bottom, #2c3e50, #2f3b4a)', color: '#fff',
    padding: 16, boxSizing: 'border-box'
  },
  main: { marginLeft: 220, padding: 20, width: '100%', boxSizing: 'border-box' },
  menu: { listStyle: 'none', padding: 0, margin: 0 },
  menuItem: { padding: '8px 0', opacity: 0.9 },
  card: {
    background: '#fff', border: '1px solid #eaeaea', borderRadius: 10,
    padding: 16, boxShadow: '0 4px 10px rgba(0,0,0,0.05)'
  },
  button: {
    padding: '10px 12px', borderRadius: 8, border: 'none', background: '#1f7a8c',
    color: '#fff', cursor: 'pointer', fontWeight: 'bold'
  }
};
