import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import { db, authReady } from '../firebase';
import { collection, onSnapshot, query, where } from 'firebase/firestore';

const AdminNavbar = () => {
  const [assignedCount, setAssignedCount] = useState(0);

  useEffect(() => {
    let unsubscribe = null;
    let active = true;
    authReady.then(() => {
      if (!active) return;
      const q = query(
        collection(db, 'emergencyRequests'),
        where('status', '==', 'assigned')
      );
      unsubscribe = onSnapshot(q, (snap) => {
        setAssignedCount(snap.size);
      });
    });
    return () => { active = false; if (unsubscribe) unsubscribe(); };
  }, []);

  return (
    <nav style={styles.navbar}>
      <h2 style={styles.logo}>Admin Panel</h2>
      <ul style={styles.navLinks}>
        <li>
          <Link style={styles.link} to="/admin">
            Dashboard
          </Link>
        </li>
        <li>
          <Link style={styles.link} to="/requests">
            Requests
          </Link>
        </li>
        <li>
          <Link style={styles.link} to="/accepted-requests">
            Accepted Requests
          </Link>
        </li>
        <li>
          <Link style={styles.link} to="/drivers">
            Drivers
          </Link>
        </li>
        <li>
          <Link style={styles.link} to="/AssignedVehicles">
            Assigned Vehicles {assignedCount > 0 && (
              <span style={styles.badge}>{assignedCount}</span>
            )}
          </Link>
        </li>

        <li>
          <Link style={styles.link} to="/">
            Logout
          </Link>
        </li>
      </ul>
    </nav>
  );
};

const styles = {
  navbar: {
    display: 'flex',
    justifyContent: 'space-between',
    alignItems: 'center',
    background: 'linear-gradient(to right, #2c3e50, #2f3b4a)',
    color: '#fff',
    padding: '14px 5%',
    position: 'sticky',
    top: 0,
    zIndex: 1000,
    flexWrap: 'wrap',
    boxSizing: 'border-box',
  },
  logo: {
    margin: 0,
    fontSize: '1.25rem',
    fontWeight: 'bold',
  },
  navLinks: {
    listStyle: 'none',
    display: 'flex',
    gap: '12px',
    margin: 0,
    padding: 0,
    flexWrap: 'wrap',
  },
  link: {
    textDecoration: 'none',
    background: 'rgba(255,255,255,0.06)',
    border: '2px solid rgba(255,255,255,0.65)',
    borderRadius: '10px',
    color: '#fff',
    fontWeight: 'bold',
    cursor: 'pointer',
    padding: '8px 14px',
    fontSize: '0.95rem',
    transition: 'all 0.2s ease',
    boxShadow: '0 2px 6px rgba(0,0,0,0.15)',
    backdropFilter: 'saturate(140%) blur(2px)',
  },
  linkHover: {
    background: 'rgba(255,255,255,0.16)',
    transform: 'translateY(-1px)',
    boxShadow: '0 4px 10px rgba(0,0,0,0.25)',
  },
  linkActive: {
    transform: 'translateY(0)',
    boxShadow: '0 2px 6px rgba(0,0,0,0.15)',
    background: 'rgba(255,255,255,0.22)',
  },
  badge: {
    marginLeft: '8px',
    backgroundColor: '#ff4444',
    color: '#fff',
    padding: '2px 8px',
    borderRadius: '999px',
    fontSize: '0.8rem',
    fontWeight: 'bold',
  },
};

export default AdminNavbar;
